function countVowels(s) {
    let count = 0;
    s = s.toLowerCase();
    for (let c of s) {
        if ("aeiou".includes(c)) count++;
    }
    return count;
}

public static int countVowels(String s) {
    int count = 0;
    s = s.toLowerCase();
    for (char c : s.toCharArray()) {
        if ("aeiou".indexOf(c) != -1) count++;
    }
    return count;
}

int countVowels(string s) {
    int count = 0;
    for (char c : s) {
        c = tolower(c);
        if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') count++;
    }
    return count;
}

